mapper.delete:
delete from DXSJCSJJLB
where CKBH = #{ckbh,jdbcType=VARCHAR}
  and GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.update:
update DXSJCSJJLB
set SWDS = #{swds,jdbcType=NUMERIC}
where CKBH = #{ckbh,jdbcType=VARCHAR}
  and GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.select:
select CKBH, GQPBH, JCSJ, SWDS
from DXSJCSJJLB
where CKBH = #{ckbh,jdbcType=VARCHAR}
  and GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}
