mapper.delete:
delete from FBSGXJCSJJLB
where JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.update:
update FBSGXJCSJJLB
set GQPBH = #{gqpbh,jdbcType=VARCHAR},
  CDYL = #{cdyl,jdbcType=NUMERIC},
  CDYB = #{cdyb,jdbcType=VARCHAR}
where JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.select:
select JCDBH, JCSJ, GQPBH, CDYL, CDYB
from FBSGXJCSJJLB
where JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}
