mapper.update:
update GQPDCSJB
set GQPMC = #{gqpmc,jdbcType=VARCHAR},
  SSSS = #{ssss,jdbcType=VARCHAR},
  SSQX = #{ssqx,jdbcType=VARCHAR},
  DCMS = #{dcms,jdbcType=VARCHAR}
where GQPBH = #{gqpbh,jdbcType=VARCHAR}

mapper.select:
select GQPBH, GQPMC, SSSS, SSQX, DCMS
from GQPDCSJB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
