mapper.update:
update GQPDSJB
set GQPMC = #{gqpmc,jdbcType=VARCHAR},
  SSQX = #{ssqx,jdbcType=VARCHAR},
  X = #{x,jdbcType=VARCHAR},
  Y = #{y,jdbcType=VARCHAR}
where GQPBH = #{gqpbh,jdbcType=VARCHAR}

mapper.select:
select GQPBH, GQPMC, SSQX, X, Y
from GQPDSJB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
