mapper.delete:
delete from GQPJCXXB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.select:
select GQPBH, JCSJ, GQPMC, JCR, SSSHENG, SSSHI, SSQX, JCTP1, JCTP2, JCTP3
from GQPJCXXB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}
