mapper.delete:
delete from GQPTZ_YCSJB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and GQPMC = #{gqpmc,jdbcType=VARCHAR}

mapper.update:
update GQPTZ_YCSJB
set SCSJ = #{scsj,jdbcType=VARCHAR},
  YWDC = #{ywdc,jdbcType=VARCHAR},
  YWLX = #{ywlx,jdbcType=VARCHAR},
  YCQX = #{ycqx,jdbcType=VARCHAR},
  YCQJ = #{ycqj,jdbcType=VARCHAR},
  PSCD = #{pscd,jdbcType=VARCHAR},
  ZTFHCD = #{ztfhcd,jdbcType=VARCHAR}
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and GQPMC = #{gqpmc,jdbcType=VARCHAR}

mapper.select:
select GQPBH, GQPMC, SCSJ, YWDC, YWLX, YCQX, YCQJ, PSCD, ZTFHCD
from GQPTZ_YCSJB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and GQPMC = #{gqpmc,jdbcType=VARCHAR}
