mapper.delete:
delete from JCDXXB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCDBH = #{jcdbh,jdbcType=VARCHAR}

mapper.update:
update JCDXXB
set JCDMC = #{jcdmc,jdbcType=VARCHAR},
  XZB = #{xzb,jdbcType=VARCHAR},
  YZB = #{yzb,jdbcType=VARCHAR}
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCDBH = #{jcdbh,jdbcType=VARCHAR}

mapper.select:
select GQPBH, JCDBH, JCDMC, XZB, YZB
from JCDXXB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCDBH = #{jcdbh,jdbcType=VARCHAR}
