mapper.delete:
delete from MGYBJJCSJJLB
where CKBH = #{ckbh,jdbcType=VARCHAR}
  and GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.update:
update MGYBJJCSJJLB
set YBDS = #{ybds,jdbcType=NUMERIC}
where CKBH = #{ckbh,jdbcType=VARCHAR}
  and GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.select:
select CKBH, GQPBH, JCDBH, JCSJ, YBDS
from MGYBJJCSJJLB
where CKBH = #{ckbh,jdbcType=VARCHAR}
  and GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}
