mapper.delete:
delete from SFSJCSJJLB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.update:
update SFSJCSJJLB
set KWMC = #{kwmc,jdbcType=VARCHAR},
  DSJ = #{dsj,jdbcType=NUMERIC},
  ZSJ = #{zsj,jdbcType=NUMERIC},
  NL = #{nl,jdbcType=NUMERIC}
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.select:
select GQPBH, JCSJ, KWMC, DSJ, ZSJ, NL
from SFSJCSJJLB
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}
