mapper.delete:
delete from SWSJB
where ZD = #{zd,jdbcType=VARCHAR}
  and SCSJ = #{scsj,jdbcType=TIMESTAMP}

mapper.update:
update SWSJB
set LY = #{ly,jdbcType=VARCHAR},
  SZSF = #{szsf,jdbcType=VARCHAR},
  LYMC = #{lymc,jdbcType=VARCHAR},
  SW = #{sw,jdbcType=NUMERIC},
  LL = #{ll,jdbcType=NUMERIC},
  JJSW = #{jjsw,jdbcType=NUMERIC}
where ZD = #{zd,jdbcType=VARCHAR}
  and SCSJ = #{scsj,jdbcType=TIMESTAMP}

mapper.select:
select ZD, SCSJ, LY, SZSF, LYMC, SW, LL, JJSW
from SWSJB
where ZD = #{zd,jdbcType=VARCHAR}
  and SCSJ = #{scsj,jdbcType=TIMESTAMP}
