mapper.insert:
insert into T_ROLE (RID, RNAME, DESCRIPTION
  )
values (#{rid,jdbcType=VARCHAR}, #{rname,jdbcType=VARCHAR}, #{description,jdbcType=VARCHAR}
  )

mapper.update:
update T_ROLE
set RNAME = #{rname,jdbcType=VARCHAR},
  DESCRIPTION = #{description,jdbcType=VARCHAR}
where RID = #{rid,jdbcType=VARCHAR}

mapper.select:
select RID, RNAME, DESCRIPTION
from T_ROLE
where RID = #{rid,jdbcType=VARCHAR}
