mapper.delete:
delete from ZYJCSJB
where JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.insert:
insert into ZYJCSJB (JCDBH, JCSJ, GQPBH, 
  GCZX, GCZY, GCZH, BQXWY, 
  BQYWY, BQHWY, BQXFXWY, 
  BQYFXWY, BQHFXWY, TBR
  )
values (#{jcdbh,jdbcType=VARCHAR}, #{jcsj,jdbcType=TIMESTAMP}, #{gqpbh,jdbcType=VARCHAR}, 
  #{gczx,jdbcType=NUMERIC}, #{gczy,jdbcType=NUMERIC}, #{gczh,jdbcType=NUMERIC}, #{bqxwy,jdbcType=NUMERIC}, 
  #{bqywy,jdbcType=NUMERIC}, #{bqhwy,jdbcType=NUMERIC}, #{bqxfxwy,jdbcType=NUMERIC}, 
  #{bqyfxwy,jdbcType=NUMERIC}, #{bqhfxwy,jdbcType=NUMERIC}, #{tbr,jdbcType=VARCHAR}
  )

mapper.select:
select JCDBH, JCSJ, GQPBH, GCZX, GCZY, GCZH, BQXWY, BQYWY, BQHWY, BQXFXWY, BQYFXWY, 
BQHFXWY, TBR
from ZYJCSJB
where JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}
