mapper.delete:
delete from ZYJCSJHJ
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}

mapper.select:
select GQPBH, JCDBH, JCSJ, JCQX, GCZX, GCZY, GCZH, BQXWY, BQYWY, BQHWY, BQXFXWY, 
BQYFXWY, BQHFXWY, ZT, YC
from ZYJCSJHJ
where GQPBH = #{gqpbh,jdbcType=VARCHAR}
  and JCDBH = #{jcdbh,jdbcType=VARCHAR}
  and JCSJ = #{jcsj,jdbcType=TIMESTAMP}
